script:
frappe.ui.form.on('Customer List',{
    refresh(frm) {

        if(frm.doc.docstatus == 1){
            frm.add_custom_button("Payment", ()=>{
		        frappe.new_doc("Payment",{
		            "customer_id":frm.doc.name,
		            "customer_name":frm.doc.name1,
		            "mobile_no":frm.doc.mobile_no,
		            "total_amount":frm.doc.grand_total
		        })
		    })
        }

        if(!frm.is_new){
            frm.set_intro("")
        }


	}
})
